package pe.spring.project.customer.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import lombok.Data;import javax.persistence.*;import javax.validation.constraints.Email;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.io.Serializable;@Entity@Table(name = "customers")@Data()public class Customer implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @NotEmpty(message = "@NotEmpty => numberID")    @Size(min = 5, max = 5, message = "@Size => numberID")    @Column(name = "number_id", unique = true, length = 5, nullable = false)    private String numberID;    @NotEmpty(message = "@NotEmpty => firstName")    @Column(name = "first_name")    private String firstName;    @NotEmpty(message = "@NotEmpty => lastName")    @Column(name = "last_name")    private String lastName;    @NotEmpty(message = "@NotEmpty => email")    @Email(message = "@Email => email")    @Column(unique = true, nullable = false)    private String email;    @NotNull(message = "@NotNull => region")    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "region_id")    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})    private Region region;    private String status;}