package pe.spring.project.customer.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import pe.spring.project.customer.entity.Customer;import pe.spring.project.customer.entity.Region;import java.util.List;@Repositorypublic interface ICustomerRepository extends JpaRepository<Customer, Long> {    Customer findByNumberID(String numberID);    List<Customer> findByLastName(String lastName);    List<Customer> findByRegion(Region region);}