package pe.spring.project.product.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;import javax.validation.constraints.NotEmpty;import javax.validation.constraints.NotNull;import javax.validation.constraints.Positive;import java.util.Date;@Entity@Table(name = "products")@Data@NoArgsConstructor@AllArgsConstructor@Builderpublic class Product {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @NotEmpty(message = "the name is required")    @Column(name = "name")    private String name;    @Column(name = "description")    private String description;    @Positive(message = "stock must be positive")    @Column(name = "stock")    private Integer stock;    @Column(name = "price")    private Double price;    @Column(name = "status")    private String status;    @Column(name = "created_at")    @Temporal(TemporalType.DATE)    private Date createdAt;    @NotNull(message = "the category must not be null")    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "category_id")    @JsonIgnoreProperties({"hibernateLazyInitializer","handler"})    private Category category;}