package pe.spring.project.product.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import pe.spring.project.product.entity.Category;import pe.spring.project.product.entity.Product;import java.util.List;@Repositorypublic interface IProductRepository extends JpaRepository<Product, Long> {    List<Product> findByCategory(Category category);}