package pe.spring.project.shopping.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import pe.spring.project.shopping.entity.Customer;@FeignClient(name = "customer-service", fallback = CustomerHystrixFallbackFactory.class)public interface CustomerClient {    @GetMapping("/customers/get/{id}")    ResponseEntity<Customer> get(@PathVariable Long id);}