package pe.spring.project.shopping.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import pe.spring.project.shopping.entity.Customer;@FeignClient(name = "customer-service")@RequestMapping("/customers")public interface CustomerClient {    @GetMapping("/get/{id}")    ResponseEntity<Customer> get(@PathVariable Long id);}