package pe.spring.project.shopping.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import pe.spring.project.shopping.entity.Product;@FeignClient(name = "product-service")@RequestMapping("/products")public interface ProductClient {    @GetMapping("/get/{id}")    ResponseEntity<Product> get(@PathVariable Long id);    @GetMapping("/edit/{id}/stock")    ResponseEntity<Product> editStock(@PathVariable Long id, @RequestParam(name = "quantity",required = true) Integer quantity);}