package pe.spring.project.shopping.entity;import lombok.Data;import javax.persistence.*;import javax.validation.constraints.Positive;@Entity@Table(name = "details")@Datapublic class Detail {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Positive(message = "message @Positive => {quantity}")    private Integer quantity;    private Double price;    @Column(name = "product_id")    private Long productId;    @Transient    private Double subTotal;    @Transient    private Product product;    public Double getSubTotal() {        if (this.price >0  && this.quantity >0 ){            return this.quantity * this.price;        }else {            return (double) 0;        }    }    public Detail() {        this.quantity=(int) 0;        this.price=(double) 0;    }}