package pe.spring.project.shopping.entity;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import lombok.Data;import javax.persistence.*;import javax.validation.Valid;import java.util.ArrayList;import java.util.Date;import java.util.List;@Entity@Table(name = "invoices")@Datapublic class Invoice {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    private String number;    private String description;    @Column(name = "customer_id")    private Long customerId;    @Column(name = "created_at")    @Temporal(TemporalType.DATE)    private Date createdAt;    private String status;    @Valid    @JsonIgnoreProperties({ "hibernateLazyInitializer", "handler" })    @OneToMany(fetch = FetchType.LAZY, cascade = CascadeType.ALL)    @JoinColumn(name = "invoice_id")    private List<Detail> details;    @Transient    private Customer customer;    public Invoice() {        details = new ArrayList<>();    }    @PrePersist    public void prePersist() {        this.createdAt = new Date();    }}