package pe.spring.project.shopping.repository;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import pe.spring.project.shopping.entity.Invoice;import java.util.List;@Repositorypublic interface IInvoiceRepository extends JpaRepository<Invoice, Long> {    List<Invoice> findByCustomerId(Long id);    Invoice findByNumber(String number);}